# coding=UTF-8from __future__ import unicode_literalsfrom django import formsfrom django.forms import ModelFormfrom . models import Informationsfrom ckeditor_uploader.fields import RichTextUploadingFormFieldfrom common.models import ImageModelclass BasicckForm(forms.Form):    """用户登录"""    name = forms.CharField(required=True, max_length=150)       # 字符串类型，必填字段，最大长度150    passwd = forms.CharField(required=True, max_length=128)     # 字符串类型，必填字段，最大长度128    content = RichTextUploadingFormField(config_name='Basic')class FullckForm(forms.Form):    """用户登录"""    name = forms.CharField(required=True, max_length=150)       # 字符串类型，必填字段，最大长度150    passwd = forms.CharField(required=True, max_length=128)     # 字符串类型，必填字段，最大长度128    content = RichTextUploadingFormField(config_name='default')class InformationsForm(ModelForm):    class Meta:        model = Informations        fields = '__all__'class AddUserForm(forms.Form):    """添加企业用户"""    username = forms.CharField(required=True, min_length=2,                               error_messages={"required": "用户名不能为空", "min_length": "用户名最少2位"})    first_name = forms.CharField(required=True, min_length=2,                                 error_messages={"required": "姓名不能为空", "min_length": "用户名最少2位"})    phone = forms.CharField(required=True, min_length=8, max_length=11,                            error_messages={"required": "电话不能为空", "min_length": "电话最少8位", "max_length":"电话最多11位"})    passwd = forms.CharField(required=True, min_length=5,                             error_messages={"required": "密码不能为空", "min_length": "密码最少5位"})    email = forms.EmailField(required=False)    gender = forms.IntegerField(required=True)    # enterprise_pk = forms.IntegerField(required=True)class AddEnterpriseForm(forms.Form):    """添加企业用户"""    username = forms.CharField(required=True, min_length=3)    first_name = forms.CharField(required=True)    user_phone = forms.CharField(required=True, min_length=8, max_length=11,                                 error_messages={"required": "电话不能为空", "min_length": "电话最少6位", "max_length": "电话最多11位"})    passwd = forms.CharField(required=True, min_length=5)    gender = forms.IntegerField(required=True)    email = forms.EmailField(required=True)    enterprise_name = forms.CharField(required=True, min_length=2)    short_name = forms.CharField(required=True, min_length=2)    operate_mode = forms.CharField(required=True)    region = forms.CharField(required=True)    address = forms.CharField(required=True, min_length=2)    enter_phone = forms.CharField(required=True, min_length=6,                                  error_messages={"required": "电话不能为空", "min_length": "电话最少6位"})    legal_repr = forms.CharField(required=True, min_length=2)    biz_scope = forms.CharField(required=True)    qyxk = forms.FileField(required=True, error_messages={'required': '药品经营许可必须'})    # 企业许可证    yyzz = forms.FileField(required=False)    # 营业执照    wts = forms.FileField(required=False)     # 法人委托书class EnterpriseQualificationForm(forms.Form):    """添加企业用户"""    qyxk = forms.FileField(required=False)    # 企业许可证    yyzz = forms.FileField(required=False)    # 营业执照    wts = forms.FileField(required=False)     # 法人委托书# 后台Logo图片上传class LogoSittingsForm(ModelForm):    class Meta:        model = ImageModel        fields = ['photo']